public cclass test1 {
	public void f() { System.out.println("f1"); }
}
public cclass test2 {
	public void f() { System.out.println("f2"); }
	public void g() { System.out.println("g2"); }
}

compile.code:
			public cclass test extends test1 & test2 {
				public void t() {
					f();
					g();
				}

				static public void main(String[] args) {
					test t = new test();
					t.t();
					test.main(null);
				}
			}

compile.binary:
			public abstract cclass A {
				protected void doA() {
					System.out.println("diamond.a");
				}
				public abstract void doAction();
			}

			public cclass B extends A {
				public void doAction() {
					doA();
					doB();
				}
				private void doB() {
					System.out.println("diamond.b");
				}
			}

			public cclass C extends A {
				public void doAction() {
					doA();
					doC();
				}
				private void doC() {
					System.out.println("diamond.C");
				}
			}

compile.code:
public cclass D extends B & C {
	private void doD() {
		System.out.println("diamond.d");
	}
	public void doAction() {
		super.doAction();
		doD();
	}
	public static void main(String args[]) {
		new D().doAction();
	}
}

