testsuite:

			public cclass A {
				public String a() { return "a"; }
			}

			public cclass B uses A {
			 	public String ba() { return "b" + a(); } 
			}

			public class Test{
				public void test(){
					B b = new B();

					// Not possible
					A a = b;
				}
			}





			public cclass A {
				public String a() { return "a"; }
			}

			public cclass B uses A {
			 	public String ba() { return "b" + a(); } 
			}

			public cclass C extends B {}

			public class Test{
				public void test(){
					C c = new C();

					// Not possible
					A a = c;
				}
			}



 (3) subtyping not possible to base class of used class
